import dispatcher from "dispatcher";




export default {
  init(data) {
    dispatcher.dispatch({actionType: "MOVIES::INIT", data});
  },

  addToMyList(id) {
    dispatcher.dispatch({actionType: "MOVIES::ADD_MOVIE_TO_MY_LIST", id});
  },

  removeFromMyList(id) {
    dispatcher.dispatch({actionType: "MOVIES::REMOVE_MOVIE_FROM_MY_LIST", id});
  }
}
