import React        from "react";
import movieActions from "actions/movie";




const {number, string, bool} = React.PropTypes;




export default React.createClass({
  displayName: "Movies",

  propTypes: {
    img:    string,
    inList: bool,
    id:     number
  },

  render() {
    let {img, inList} = this.props;

    return  <div className="Movie">
              <img src={img}/>
              {inList
                ? <button onClick={this.removeFromMyList}>Remove</button>
                : <button onClick={this.addToMyList}>Add</button>
              }
            </div>
  },

  addToMyList(e) {
    e.preventDefault();
    movieActions.addToMyList(this.props.id);
  },

  removeFromMyList(e) {
    e.preventDefault();
    movieActions.removeFromMyList(this.props.id);
  }
})

